import { _idivmod_limb , _idivmod_schoolbook , _idivmod_dc } from '../core' ;
import { THRESHOLD_DIV_DC } from '../core' ;

/**
 * Computes the quotient and remainder of two numbers. Uses the most
 * appropriate algorithms depending on the size of the operands. The remainder
 * is written to the dividend array. There are a few assumptions made on the
 * input.
 *
 * Input
 * -----
 *  - No leading zeros in D.
 *  - |D| = |Q|
 *  - Q is zero initialized.
 *
 * @param {Number} r The base to work with.
 * @param {Array} D Dividend / Remainder array (remainder computed in-place).
 * @param {Number} Di Left of dividend.
 * @param {Number} Dj Right of dividend.
 * @param {Array} d Divisor array.
 * @param {Number} di Left of divisor.
 * @param {Number} dj Right of divisor.
 * @param {Array} Q Quotient array (zero initialized).
 * @param {Number} Qi Left of quotient.
 * @param {Number} Qj Right of quotient.
 */
export function _idivmod ( r , D , Di , Dj , d , di , dj , Q , Qi , Qj ) {

	const dn = dj - di ;

	if ( dn === 1 ) {
		return _idivmod_limb( r , d[di] , D , Di , Dj , Q , Qi ) ;
	}

	else if ( dn < THRESHOLD_DIV_DC ) {
		return _idivmod_schoolbook( r , D , Di , Dj , d , di , dj , Q , Qi ) ;
	}

	else {
		return _idivmod_dc( r , D , Di , Dj , d , di , dj , Q , Qi , Qj ) ;
	}

}

import { _ADD } from '../core/arithmetic' ;

/**
 * Adds two big endian arrays and puts result in a destination array.
 * Wraps on overflow. Works with any combination of array sizes.
 *
 * @param {Number} r base (radix)
 * @param {Array} a first operand
 * @param {Number} ai a left
 * @param {Number} aj a right
 * @param {Array} b second operand
 * @param {Number} bi b left
 * @param {Number} bj b right
 * @param {Array} c result, must be 0 initialized
 * @param {Number} ci c left
 * @param {Number} cj c right
 *
 */

export function add ( r , a , ai , aj , b , bi , bj , c , ci , cj ) {

	ci = Math.max( 0 , ci );
	const k = cj - ci;

	ai = Math.max( 0 , ai , aj - k ) ;
	bi = Math.max( 0 , bi , bj - k ) ;
	const m = aj - ai;
	const n = bj - bi;

	return m < n ?
		_ADD( r , b , bi , bj , a , ai , aj , c , ci , cj ) :
		_ADD( r , a , ai , aj , b , bi , bj , c , ci , cj ) ;

}

import { _IADD } from '../core/arithmetic' ;

/**
 * Adds a big endian array to another ___in-place___.
 * Wraps on overflow. Works with any combination of array sizes.
 *
 * @param {Number} r base (radix)
 * @param {Array} a first operand (modified in-place)
 * @param {Number} ai a left
 * @param {Number} aj a right
 * @param {Array} b second operand
 * @param {Number} bi b left
 * @param {Number} bj b right
 */
export function iadd ( r , a , ai , aj , b , bi , bj ) {

	const m = aj - ai ;

	return _IADD( r , a , ai , aj , b , Math.max( bi , bj - m ) , bj ) ;

}

export * from './_divmod' ;
export * from './_idivmod' ;
export * from './add' ;
export * from './iadd' ;
export * from './mul' ;

import { _mul } from '../core/arithmetic' ;

/**
 *  - C is zero initialized,
 *  - |A| >= 0,
 *  - |B| >= 0,
 *  - |C| >= |A| + |B|.
 */

export function mul ( r , a , ai , aj , b , bi , bj , c , ci , cj ) {

	// TODO Truncate input if output is too small (see api/add)?

	return aj - ai >= bj - bi ?
		_mul( r , a , ai , aj , b , bi , bj , c , ci , cj ) :
		_mul( r , b , bi , bj , a , ai , aj , c , ci , cj ) ;

}

/**
 * Adds two big endian arrays and puts result in a destination array.
 * Wraps on overflow. |C| >= |A| >= |B|.
 *
 * @param {Number} r base (radix)
 * @param {Array} a first operand
 * @param {Number} ai a left
 * @param {Number} aj a right
 * @param {Array} b second operand
 * @param {Number} bi b left
 * @param {Number} bj b right
 * @param {Array} c result, must be 0 initialized
 * @param {Number} ci c left
 * @param {Number} cj c right
 */

export function _ADD ( r , a , ai , aj , b , bi , bj , c , ci , cj ) {

	let C = 0 ;

	while ( --bj >= bi ) {
		const t = a[--aj] + b[bj] + C ;
		c[--cj] = t % r ;
		C = t >= r ;
	}

	while ( --aj >= ai ) {
		const t = a[aj] + C ;
		c[--cj] = t % r ;
		C = t >= r ;
	}

	if ( --cj >= ci ) c[cj] = +C ;

}

/**
 * Adds a big endian array to another.
 * Wraps on overflow. |A| >= |B|.
 *
 * @param {Number} r base (radix)
 * @param {Array} a first operand
 * @param {Number} ai a left
 * @param {Number} aj a right
 * @param {Array} b second operand
 * @param {Number} bi b left
 * @param {Number} bj b right
 */

export function _IADD ( r , a , ai , aj , b , bi , bj ) {

	let C = 0 ;

	while ( --bj >= bi ) {
		const T = a[--aj] + b[bj] + C ;
		a[aj] = T % r ;
		C = T >= r ;
	}

	while ( --aj >= ai ) {
		const T = a[aj] + C ;
		a[aj] = T % r ;
		C = T >= r ;
	}

}

/**
 * Adds 1 to a big endian array.
 *
 * @param {Number} r radix
 * @param {Array} a first operand
 * @param {Number} ai a left
 * @param {Number} aj a right
 */
export function _increment ( r , a , ai , aj ) {

	const _r = r - 1 ;

	while ( --aj >= ai ) {

		if ( a[aj] < _r ) {
			++a[aj] ;
			return ;
		}

		a[aj] = 0 ;

	}

}

export * from './_ADD' ;
export * from './_IADD' ;
export * from './_increment' ;

/**
 * Divides a big endian number by a single limb number.
 * Can only work with limbs of size at most sqrt( 2^53 ).
 * Allows to prefix the dividend with an intermediate remainder.
 *
 * Does not update the remainder.
 *
 * Input
 * -----
 *  - |Q| = |D|
 *
 * @param {Number} r The radix.
 * @param {Number} tmp Intermediate remainder (MUST be <code>< d</code>).
 * @param {Number} d The divisor.
 * @param {Array} D The dividend (NOT modified).
 * @param {Number} Di Left of dividend.
 * @param {Number} Dj Right of dividend.
 * @param {Array} Q The quotient.
 * @param {Number} Qi Left of quotient.
 */
export function _div_limb_with_prefix ( r , tmp , d , D , Di , Dj , Q , Qi ) {

	while ( Di < Dj ) {

		tmp *= r ; tmp += D[Di] ;

		Q[Qi] = tmp / d | 0 ;
		tmp %= d ;

		++Qi ; ++Di ;

	}

}

import { _zeros , _copy } from '../../array' ;
import { _imul_limb } from '../mul' ;
import { _idivmod_dc_21 , _div_limb_with_prefix , _mod_limb } from '.' ;
import { _CMP_n } from '../../compare' ;


/**
 * Input
 * -----
 *  - No leading zeros
 *  - |A| = |C|
 *
 * References
 * ----------
 *   - https://gmplib.org/manual/Divide-and-Conquer-Division.html
 *
 * @param {Number} X The radix.
 * @param {Array} a Dividend / Remainder.
 * @param {Number} ai
 * @param {Number} aj
 * @param {Array} b Divisor.
 * @param {Number} bi
 * @param {Number} bj
 * @param {Array} c Quotient.
 * @param {Number} ci
 * @param {Number} cj
 */
export function _idivmod_dc ( X , a , ai , aj , b , bi , bj , c , ci , cj ) {

	// [BZ98] Fast Recursive Division

	const r = aj - ai ;
	const s = bj - bi ;

	if ( r < s || r === s && _CMP_n( a , ai , aj , b , bi ) < 0 ) return ;

	// shift to get n = 2^k for some k
	let _n = 1 ;

	while ( _n  < s ) _n <<= 1 ;

	const n = _n ;

	const shift = n - s ;

	const x = b[bi] ;
	const _X = X / 2 ;
	const _normalize = x < _X ;
	const z = Math.ceil( _X / x ) ;

	const w = r + shift + ( _normalize || a[ai] >= _X ) ;
	const t = Math.ceil( w / n ) ;
	const _ai = 0 ;
	const _aj = t * n ;            // + 1 if
	const _a = _zeros( _aj ) ;     // potential normalization overflow
	const _ak = _aj - shift - r ;  // or if A potentially bigger than B
	_copy( a , ai , aj , _a , _ak ) ;

	const _bi = 0 ;
	const _bj = n ;
	const _b = _zeros( n ) ;
	_copy( b , bi , bj , _b , 0 ) ;

	if ( _normalize ) {

		_imul_limb( X , z , _a , _ai , _aj ) ;
		_imul_limb( X , z , _b , _bi , _bj ) ;

	}

	const _cj = _aj ;
	const _c = _zeros( _cj ) ;

	for ( let i = 0 ; i < _aj - n ; i += n ) {

		_idivmod_dc_21( X , _a , i , i + ( n << 1 ) , _b , _bi , _bj , _c , i , i + ( n << 1 ) ) ;

	}

	if ( _normalize ) {
		const p = _mod_limb( X , z , _a , _ai , _ak ) ;
		_div_limb_with_prefix( X , p , z , _a , _ak , _aj - shift , a , ai , aj ) ;
	}
	else {
		_copy( _a , _ak , _aj - shift , a , ai , aj ) ;
	}

	_copy( _c , _cj - r , _cj , c , ci ) ;

}

import { THRESHOLD_DIV_DC } from '../../thresholds' ;
import { _idivmod_dc_32 , _idivmod_schoolbook_large_divisor } from '.' ;


/**
 * Algorithm 3.3 Divide-and-conquer division (2 by 1)
 * ==================================================
 *
 * Input
 * -----
 *  Two nonnegative integers A and B,
 *  such that A < ^n B and ^n / 2  B < ^n.
 *  n must be even.
 *
 *                    -----------                 -----
 *                   |  :  |  :  |               |  :  |
 *                    -----------                 -----
 *
 * Output
 * ------
 *  The quotient floor( A/B ) and the remainder A mod B.
 *
 * Complexity
 * ----------
 *  T(n) = 2T'(n/2) + K
 *
 */
export function _idivmod_dc_21 ( r , a , ai , aj , b , bi , bj , c , ci , cj ) {

	if ( bj - bi < THRESHOLD_DIV_DC ) {
		return _idivmod_schoolbook_large_divisor( r , a , ai , aj , b , bi , bj , c , ci ) ;
	}

	// 1. Let A = A_3 ^{3n/2} + A_2 ^n + A_1 ^{n/2} + A_0 and
	//    B = B_1 ^{n/2} + B_0,
	//    with 0  A_i < ^{n/2} and 0  B_i < ^{n/2}.

	const m = aj - ai ;
	const k = m >>> 2 ;

	// 2. Compute the high half Q_1 of the quotient as
	//    Q_1 = ( A_3 ^n + A_2 ^{n/2} + A_1 ) / B
	//    with remainder R_1 using algorithm 3.4.

	_idivmod_dc_32( r , a , ai , aj - k , b , bi , bj , c , ci , cj - k ) ;

	// 3. Compute the low half Q_0 of the quotient as
	//    Q_0 = ( R_1 ^{n/2} + A_0 ) / B
	//    with remainder R_0 using algorithm 3.4.

	_idivmod_dc_32( r , a , ai + k , aj , b , bi , bj , c , ci + k , cj ) ;

	// 4. Return the quotient Q = Q_1 ^{n/2} + Q_0 and the remainder R = R_0 .

}

import { _zeros , _fill } from '../../array' ;
import { _decrement , _isub } from '../sub' ;
import { _mul } from '../mul' ;
import { _lt } from '../../compare' ;
import { iadd } from '../../../api' ;
import { _idivmod_dc_21 } from '.' ;

/**
 * Algorithm 3.4 Divide-and-conquer division (3 by 2)
 * ==================================================
 *
 * Input
 * -----
 *  Two nonnegative integers A and B,
 *  such that A < ^n B and ^{2n} / 2  B < ^{2n}.
 *  n must be even.
 *
 *                    --------                 -----
 *                   |  |  |  |               |  |  |
 *                    --------                 -----
 *
 * Output
 * ------
 *  The quotient floor( A/B ) and the remainder A mod B.
 *
 * Complexity
 * ----------
 *  T'(n)  T(n) + M(n) + Ln
 *
 */
export function _idivmod_dc_32 ( r , a , ai , aj , b , bi , bj , c , ci , cj ) {

	// 1. Let A = A_2 ^{2n} + A_1 ^n + A_0 and
	//    B = B_1 ^{n} + B_0,
	//    with 0  A_i < ^n and 0  B_i < ^n.

	const k = bj - bi ;
	const n = k >>> 1 ;

	// 2. If A_2 < B_1, compute Q = floor( ( A_2 ^n + A_1 ) / B_1 ) with
	//    remainder R_1 using algorithm 3.3;

	if ( _lt( a , ai , ai + n , b , bi , bi + n ) ) {
		_idivmod_dc_21( r , a , ai , aj - n , b , bi , bi + n , c , ci + n , cj ) ;
	}

	//    otherwise let Q = ^n - 1, and R_1 = ( A_2 - B_1 ) ^n + A_1 + B_1
	//    (note in this case that A_2 = B_1)

	else {
		_fill( c , cj - n , cj , r - 1 ) ;
		iadd( r , a , ai , aj - n , b , bi , bi + n  ) ;
		_isub( r , a , ai , ai + n , b , bi , bi + n  ) ;
	}

	// 3. R <- R_1 ^n + A_0 - Q*B_0

	const zi = 0 ;
	const zj = n << 1 ;
	const z = _zeros( zj ) ;
	_mul( r , c , cj - n , cj , b , bi + n , bj , z , zi , zj ) ;
	_isub( r , a , ai , aj , z , zi , zj ) ;

	// 4. if R < 0 , R <- R + B and Q <- Q - 1

	if ( a[ai] === 0 ) return ;
	iadd( r , a , ai , aj , b , bi , bj ) ;
	_decrement( r , c , cj - n , cj ) ;

	// 5. if R < 0 , R <- R + B and Q <- Q - 1

	if ( a[ai] === 0 ) return ;
	iadd( r , a , ai , aj , b , bi , bj ) ;
	_decrement( r , c , cj - n , cj ) ;

	// 6. Return Q and R

}

import { _idivmod_limb_with_prefix } from '.' ;

/**
 * Divides a big endian number by a single limb number.
 * Can only work with limbs of size at most sqrt( 2^53 ).
 *
 * @param {Number} r The radix.
 * @param {Number} d The divisor.
 * @param {Array} D The dividend.
 * @param {Number} Di Left of dividend.
 * @param {Number} Dj Right of dividend.
 * @param {Array} Q The quotient.
 * @param {Number} Qi Left of quotient.
 */
export function _idivmod_limb ( r , d , D , Di , Dj , Q , Qi ) {

	// simply prefix the dividend with 0
	_idivmod_limb_with_prefix( r , 0 , d , D , Di , Dj , Q , Qi ) ;

}

/**
 * Divides a big endian number by a single limb number.
 * Can only work with limbs of size at most sqrt( 2^53 ).
 * Allows to prefix the dividend with an intermediate remainder.
 *
 * Input
 * -----
 *  - |Q| = |D|
 *
 * @param {Number} r The radix.
 * @param {Number} tmp Intermediate remainder (MUST be <code>< d</code>).
 * @param {Number} d The divisor.
 * @param {Array} D The dividend.
 * @param {Number} Di Left of dividend.
 * @param {Number} Dj Right of dividend.
 * @param {Array} Q The quotient.
 * @param {Number} Qi Left of quotient.
 */
export function _idivmod_limb_with_prefix ( r , tmp , d , D , Di , Dj , Q , Qi ) {

	while ( Di < Dj ) {

		tmp *= r ; tmp += D[Di] ;

		Q[Qi] = tmp / d | 0 ;
		tmp %= d ;
		D[Di] = 0 ;

		++Qi ; ++Di ;

	}

	D[Dj-1] = tmp ;

}

import { _zeros , _copy } from '../../array' ;
import { _mul_limb } from '../mul' ;
import { _idivmod_schoolbook_large_divisor , _div_limb_with_prefix } from '.' ;

/**
 * Computes q <- a / b and a <- a % b.
 * No leading zeros allowed.
 * q has length at least aj - ai
 *
 * @param {Number} r The radix.
 * @param {Array} a Dividend / Remainder.
 * @param {Number} ai
 * @param {Number} aj
 * @param {Array} b Divisor.
 * @param {Number} bi
 * @param {Number} bj
 * @param {Array} q Quotient.
 * @param {Number} qi
 */
export function _idivmod_schoolbook ( r , a , ai , aj , b , bi , bj , q , qi ) {

	const _r = Math.ceil( r / 2 ) ;
	const x = b[bi] ;

	if ( x < _r ) {

		// we need x to be >= _r so we multiply b by ceil( _r / x )
		// this gives us <= ( 1 + _r / x ) b < r^(bj-bi)
		// (this can be implemented faster using bit shifts if r = 2^k )
		const z = Math.ceil( _r / x ) ;
		const m = aj - ai + 1 ;
		const n = bj - bi ;

		const _a = _zeros( m ) ;
		_mul_limb( r , z , a , ai , aj , _a , 0 , m ) ;

		const _b = _zeros( n ) ;
		_mul_limb( r , z , b , bi , bj , _b , 0 , n ) ;

		const _q = _zeros( m ) ;
		_idivmod_schoolbook_large_divisor( r , _a , 0 , m , _b , 0 , n , _q , 0 ) ;
		_div_limb_with_prefix( r , _a[0] , z , _a , 1 , m , a , ai ) ;
		_copy( _q , 1 , m , q , qi ) ;

	}

	else _idivmod_schoolbook_large_divisor( r , a , ai , aj , b , bi , bj , q , qi ) ;

}

import { _trim_positive } from '../../convert' ;
import { _lt } from '../../compare' ;
import { _isub } from '..' ;
import { _idivmod_schoolbook_subroutine } from '.' ;

/**
 * Input
 * -----
 *  - Two integers A and B such that r^(m-1) <= A < r^m and (r^n)/2 <= B < r^(n).
 *  - No leading zeros
 *
 * Output
 * -----
 *  The quotient floor( A/B ) and the remainder A mod B.
 *
 * @param {Number} r The radix.
 * @param {Array} a Dividend.
 * @param {Number} ai Left of dividend.
 * @param {Number} aj Right of dividend.
 * @param {Array} b Divisor.
 * @param {Number} bi Left of divisor.
 * @param {Number} bj Right of divisor.
 * @param {Array} q Quotient.
 * @param {Number} qi Left of quotient.
 */
export function _idivmod_schoolbook_large_divisor ( r , a , ai , aj , b , bi , bj , q , qi ) {

	const m = aj - ai ;
	const n = bj - bi ;

	// If m < n, return the quotient 0 and the remainder A.
	if ( m < n ) return ;

	if ( m === n ) {

		// If m = n, then if A < B, return the quotient 0 and the remainder A;
		if ( _lt( a , ai , aj , b , bi , bj ) ) return ;

		// if A  B, return the quotient 1 and the remainder A - B.
		++q[qi+m-1] ;
		_isub( r , a , ai , aj , b , bi , bj ) ;
		return ;

	}

	// If m = n + 1, compute the quotient and remainder of A/B
	// using algorithm 3.1 and return them.
	if ( m === n + 1 ) return _idivmod_schoolbook_subroutine( r , a , ai , aj , b , bi , bj , q , qi ) ;


	// 4. A' <- A/^{m-n-1} and s <- A mod ^{m-n-1}
	const _aj = ai + n + 1 ;

	// 5. Compute the quotient q' and the remainder r' of A'/B using algorithm 3.1.
	_idivmod_schoolbook_subroutine( r , a , ai , _aj , b , bi , bj , q , qi ) ;

	// 6. Compute the quotient q and remainder r of( ^{m-n-1} r' + s ) / B recursively.
	const ak = _trim_positive( a , ai , _aj ) ;
	_idivmod_schoolbook_large_divisor( r , a , ak , aj , b , bi , bj , q , qi + ak - ai ) ;

	// 7. Return the quotient Q = ^{m-n-1} q' + q and remainder R = r

}

import { _zeros } from '../../array' ;
import { _ge , _gt } from '../../compare' ;
import { _increment } from '..' ;
import { _isub } from '..' ;
import { _mul_limb } from '..' ;

/**
 * Input
 * -----
 *  - Two integers A and B such that 0 <= A < r^n+1 and (r^n)/2 <= B < r^(n).
 *  - |a| = |b| + 1
 *  - |q| = |a|
 *
 * Output
 * -----
 *  The quotient floor( A/B ) and the remainder A mod B.
 *
 * @param {Number} r The radix.
 * @param {Array} a Dividend.
 * @param {Number} ai Left of dividend.
 * @param {Number} aj Right of dividend.
 * @param {Array} b Divisor.
 * @param {Number} bi Left of divisor.
 * @param {Number} bj Right of divisor.
 * @param {Array} q Quotient.
 * @param {Number} qi Left of quotient.
 */
export function _idivmod_schoolbook_subroutine ( r , a , ai , aj , b , bi , bj , q , qi ) {

	const m = aj - ai ;

	// If A  B*, compute the quotient q and remainder r of ( A  B* ) / B
	// recursively, and return  + q and r.
	if ( _ge( a , ai , aj - 1 , b , bi , bj ) ) {
		_isub( r , a , ai , aj - 1 , b , bi , bj ) ;
		_idivmod_schoolbook_subroutine( r , a , ai , aj , b , bi , bj , q , qi ) ;
		_increment( r , q , qi , qi + m - 1 ) ;
		return ;
	}

	// If A < B*, then A/B < 
	// q <- min [ (  a_0 + a_1 ) / b_0 ,  - 1 ]
	let _q = Math.min( r - 1 , Math.floor( ( a[ai] * r + a[ai+1] ) / b[bi] ) ) ;

	// fix _q
	const T = _zeros( m ) ;
	_mul_limb( r , _q , b , bi , bj , T , 0 , m ) ;

	if ( _gt( T , 0 , m , a , ai , aj ) ) {
		--_q ;
		_isub( r , T , 0 , m , b , bi , bj ) ;

		if ( _gt( T , 0 , m , a , ai , aj ) ) {
			--_q ;
			_isub( r , T , 0 , m , b , bi , bj ) ;
		}

	}

	q[qi + m - 1] += _q ;

	_isub( r , a , ai , aj , T , 0 , m ) ;

}

import { _sub } from '..' ;
import { _lt } from '../..' ;

/**
 * Computes quotient and remainder of two big endian arrays.
 * <p>
 * Computes quotient and remainder of two big endian arrays
 * using long division algorithm (the one teached in
 * european primary schools).
 *
 * /!\ This algorithm modifies its first operand.
 *
 * HYP : q is at least as large as r
 *       b is not zero
 *
 * @param {int} x the radix
 * @param {array} r dividend and remainder
 * @param {int} ri r left
 * @param {int} rj r right
 * @param {array} b divisor
 * @param {int} bi b left
 * @param {int} bj b right
 * @param {array} q quotient, must be 0 initialized
 * @param {int} qi q left
 */

// /!\ There are implicit hypotheses
//     made on the size of the operands.
//     Should clarify.

export function _idivmod_slow ( x , r , ri , rj , b , bi , bj , q , qi ) {

	var k, t = ri + 1;

	do {

		// trim leading zeros
		// TODO maybe could try to put this procedure inside the _sub loop
		// TODO or assume that the number is trimed at the begining
		//      and put this statement at the end of the main loop
		while (ri < rj && r[ri] === 0) ++ri;

