		// TODO maybe could try binary search on the _lt function
		//      for another implementation
		k = ri + 1;
		while (k <= rj && _lt(r, ri, k, b, bi, bj)) ++k;

		// remainder smaller than divisor --> end
		if (k > rj) break;

		// divide current block interval by quotient
		do{

			// increment quotient block corresponding
			// to current ls block of remainder interval
			++q[qi + k - t];

			// subtract divisor from current remainder
			// block interval
			_sub(x, r, ri, k, b, bi, bj, r, ri, k);

		} while(!_lt(r, ri, k, b, bi, bj));


	} while(true);

}

/**
 * Divides a big endian number by a single limb number and returns only the
 * remainder. Can only work with limbs of size at most sqrt( 2^53 ).
 *
 * @param {Number} r The radix of D.
 * @param {Number} d The divisor.
 * @param {Array} D The dividend (NOT modified).
 * @param {Number} Di Left of D.
 * @param {Number} Dj Right of D.
 * @returns {Number} The remainder D % d.
 */
export function _mod_limb ( r , d , D , Di , Dj ) {

	let R = 0 ;

	while ( Di < Dj ) {

		R *= r ;
		R += D[Di] ;
		R %= d ;
		++Di ;

	}

	return R ;

}

