/**
 * C is zero initialized
 * |A| >= |B| >= 1, |C| >= |A| + |B|.
 * TODO use schoolbook mul if n = O(log m)
 */

export function _mul ( r , a , ai , aj , b , bi , bj , c , ci , cj ) {

	//const m = aj - ai ;
	const n = bj - bi ;

	// TODO then |B| = 1 and could be faster
	//if ( m === 1 ) return _mul_limb( r , a[ai] , b , bi , bj , c , ci , cj ) ;

	if ( n === 1 ) return _mul_limb( r , b[bi] , a , ai , aj , c , ci , cj ) ;

	//if ( m === n ) {

		//if ( a === b && ai === bi ) return _sqr( r , a , ai , aj , c , ci , cj ) ;

		//return _mul_n( r , a , ai , aj , b , bi , bj , c , ci , cj ) ;

	//}

	if ( n < THRESHOLD_MUL_TOOM22 ) {
		return _schoolbook_mul( r , a , ai , aj , b , bi , bj , c , ci , cj ) ;
	}

	return _karatsuba( r , a , ai , aj , b , bi , bj , c , ci , cj ) ;


}

/**
 * Compute x * b where x is a single limb.
 */

export function _mul_limb ( r , x , b , bi , bj , c , ci , cj ) {

	let C = 0 ;

	while ( true ) {

		--bj ;
		--cj ;

		if ( bj < bi ) {
			if ( cj >= ci ) c[cj] = C ;
			return ;
		}

		if ( cj < ci ) return ;

		const t = b[bj] * x + C ;
		const u = t % r ;

		c[cj] = u ;

		C = (t - u) / r ;

	}

}

/**
 * Computes the product of two big endian arrays using schoolbook
 * multiplication. |C| >= |A|+|B|.
 *
 * TODO Can this be optimized if we know that |A| >= |B|?
 * Probably better to do many small passes rather than few large passes ?!
 * This is what this implementation achieves, although it returns correct
 * results even when |A| < |B|.
 */

export function _schoolbook_mul ( r , a , ai , aj , b , bi , bj , c , ci , cj ) {

	const m = aj - ai ;
	const n = bj - bi ;
	--aj ;
	--bj ;
	--cj ;

	for ( let i = 0 ; i < m ; ++i ) {

		let q = 0 ;

		for ( let j = 0 ; j < n ; ++j ) {

			const t = c[cj-i-j] + q + a[aj-i] * b[bj-j] ;
			const s = t % r ;
			c[cj-i-j] = s ;
			q = ( t - s ) / r ;

		}

		c[cj-i-n] = q ;

	}

}

