import _add from '../../../core/arithmetic/add/_add.js';

/**
 * Adds two big endian arrays and puts result in a destination array.
 * Wraps on overflow. Works with any combination of array sizes.
 *
 * @param {Number} r base (radix)
 * @param {Array} a first operand
 * @param {Number} ai a left
 * @param {Number} aj a right
 * @param {Array} b second operand
 * @param {Number} bi b left
 * @param {Number} bj b right
 * @param {Array} c result, must be 0 initialized
 * @param {Number} ci c left
 * @param {Number} cj c right
 *
 */
export default function add(r, a, ai, aj, b, bi, bj, c, ci, cj) {
	ci = Math.max(0, ci);
	const k = cj - ci;

	ai = Math.max(0, ai, aj - k);
	bi = Math.max(0, bi, bj - k);
	const m = aj - ai;
	const n = bj - bi;

	return m < n
		? _add(r, b, bi, bj, a, ai, aj, c, ci, cj)
		: _add(r, a, ai, aj, b, bi, bj, c, ci, cj);
}

import _iadd from '../../../core/arithmetic/add/_iadd.js';

/**
 * Adds a big endian array to another ___in-place___.
 * Wraps on overflow. Works with any combination of array sizes.
 *
 * @param {Number} r base (radix)
 * @param {Array} a first operand (modified in-place)
 * @param {Number} ai a left
 * @param {Number} aj a right
 * @param {Array} b second operand
 * @param {Number} bi b left
 * @param {Number} bj b right
 */
export default function iadd(r, a, ai, aj, b, bi, bj) {
	const m = aj - ai;

	return _iadd(r, a, ai, aj, b, Math.max(bi, bj - m), bj);
}

import assert from 'assert';

/**
 * Adds 1 to a big endian array.
 *
 * Wraps on overflow. Hence, does nothing if aj <= ai.
 *
 * O(|A|) time in the worst case.
 * O(1) amortized time over any number of successive operations starting with A = O(1).
 * O(1) amortized time over O(|A|) successive operations starting with any A.
 *
 * @param {Number} r radix
 * @param {Array} a first operand
 * @param {Number} ai a left
 * @param {Number} aj a right
 */
export default function increment(r, a, ai, aj) {
	assert(r >= 2);
	assert(ai >= 0 && aj <= a.length);

	const _r = r - 1;

	while (--aj >= ai) {
		if (a[aj] < _r) {
			++a[aj];
			return;
		}

		a[aj] = 0;
	}
}

import assert from 'assert';

import _idivmod_limb from '../../../core/arithmetic/div/_idivmod_limb.js';
import _idivmod_schoolbook from '../../../core/arithmetic/div/_idivmod_schoolbook.js';
import _idivmod_dc from '../../../core/arithmetic/div/_idivmod_dc.js';
import THRESHOLD_DIV_DC from '../../../core/thresholds/THRESHOLD_DIV_DC.js';
import jz from '../../compare/jz.js';

/**
 * Computes the quotient and remainder of two numbers. Uses the most
 * appropriate algorithm depending on the size of the operands. The remainder
 * is written to the dividend array. There are a few assumptions made on the
 * input.
 *
 * Input
 * -----
 *  - |d| >= 1
 *  - |D| = |Q| >= 1
 *  - No leading zeros in D or d.
 *  - Q is zero initialized.
 *
 * @param {Number} r The base to work with.
 * @param {Array} D Dividend / Remainder array (remainder computed in-place).
 * @param {Number} Di Left of dividend.
 * @param {Number} Dj Right of dividend.
 * @param {Array} d Divisor array.
 * @param {Number} di Left of divisor.
 * @param {Number} dj Right of divisor.
 * @param {Array} Q Quotient array (zero initialized).
 * @param {Number} Qi Left of quotient.
 * @param {Number} Qj Right of quotient.
 */
export default function _idivmod(r, D, Di, Dj, d, di, dj, Q, Qi, Qj) {
	assert(r >= 2);

	assert(Di >= 0 && Dj <= D.length);
	assert(di >= 0 && dj <= d.length);
	assert(Qi >= 0 && Qj <= Q.length);

	assert(dj - di >= 1);
	assert(Dj - Di === Qj - Qi);
	assert(Qj - Qi >= 1);

	assert(D[Di] !== 0);
	assert(d[di] !== 0);
	assert(jz(Q, Qi, Qj));

	const dn = dj - di;

	if (dn === 1) {
		return _idivmod_limb(r, d[di], D, Di, Dj, Q, Qi);
	}

	if (dn < THRESHOLD_DIV_DC) {
		return _idivmod_schoolbook(r, D, Di, Dj, d, di, dj, Q, Qi);
	}

	return _idivmod_dc(r, D, Di, Dj, d, di, dj, Q, Qi, Qj);
}

import assert from 'assert';

import _reset from '../../../core/array/_reset.js';
import _imod_limb from '../../../core/arithmetic/div/_imod_limb.js';
import _imod_schoolbook from '../../../core/arithmetic/div/_imod_schoolbook.js';
import _idivmod_dc from '../../../core/arithmetic/div/_idivmod_dc.js';
import THRESHOLD_DIV_DC from '../../../core/thresholds/THRESHOLD_DIV_DC.js';

/**
 * Computes the remainder of two numbers. Uses the most
 * appropriate algorithm depending on the size of the operands. The remainder
 * is written to the dividend array. There are a few assumptions made on the
 * input.
 *
 * Input
 * -----
 *  - |d| >= 1
 *  - |D| = |_| >= 1
 *  - No leading zeros in D or d.
 *
 * @param {Number} r The base to work with.
 * @param {Array} D Dividend / Remainder array (remainder computed in-place).
 * @param {Number} Di Left of dividend.
 * @param {Number} Dj Right of dividend.
 * @param {Array} d Divisor array.
 * @param {Number} di Left of divisor.
 * @param {Number} dj Right of divisor.
 * @param {Array} _ Additional memory array.
 * @param {Number} _i Left of memory.
 * @param {Number} _j Right of memory.
 */
export default function _imod(r, D, Di, Dj, d, di, dj, _, _i, _j) {
	assert(r >= 2);

	assert(Di >= 0 && Dj <= D.length);
	assert(di >= 0 && dj <= d.length);
	assert(_i >= 0 && _j <= _.length);

	assert(dj - di >= 1);
	assert(Dj - Di === _j - _i);
	assert(_j - _i >= 1);

	assert(D[Di] !== 0);
	assert(d[di] !== 0);

	const dn = dj - di;

	if (dn === 1) {
		return _imod_limb(r, d[di], D, Di, Dj);
	}

	if (dn < THRESHOLD_DIV_DC) {
		return _imod_schoolbook(r, D, Di, Dj, d, di, dj);
	}

	_reset(_, _i, _j);
	return _idivmod_dc(r, D, Di, Dj, d, di, dj, _, _i, _j);
}

import _trim_positive from '../../../core/convert/_trim_positive.js';
import _alloc from '../../../core/array/_alloc.js';
import _copy from '../../../core/array/_copy.js';

import _cmp_n from '../../../core/compare/_cmp_n.js';
import _euclidean_algorithm_loop from '../../../core/arithmetic/gcd/_euclidean_algorithm_loop.js';

/**
 * No constraints on the input.
 *
 * @param {Number} r The radix.
 * @param {Array} a First input number <code>a>b</code>.
 * @param {Number} ai <code>a</code> left bound.
 * @param {Number} aj <code>a</code> right bound.
 * @param {Array} b Second input number <code>b<a</code>.
 * @param {Number} bi <code>b</code> left bound.
 * @param {Number} bj <code>b</code> right bound.
 * @returns {Array} The array containing the gcd of A and B (one of A and B).
 * Return as [ d , di , dj ], where d is the array and di and dj are its left
 * and right bounds.
 */
export default function euclidean_algorithm(r, a, ai, aj, b, bi, bj) {
	const _ai = _trim_positive(a, ai, aj);
	const _bi = _trim_positive(b, bi, bj);
	const m = aj - _ai;
	const n = bj - _bi;

	const R0 = _alloc(m);
	_copy(a, _ai, aj, R0, 0);
	const R1 = _alloc(n);
	_copy(b, _bi, bj, R1, 0);

	return m > n || (m === n && _cmp_n(R0, 0, m, R1, 0) >= 0)
		? _euclidean_algorithm_loop(r, R0, 0, m, R1, 0, n)
		: _euclidean_algorithm_loop(r, R1, 0, n, R0, 0, m);
}

import _trim_positive from '../../../core/convert/_trim_positive.js';
import _cmp_n from '../../../core/compare/_cmp_n.js';
import _extended_euclidean_algorithm from '../../../core/arithmetic/gcd/_extended_euclidean_algorithm.js';

/**
 * No constraints on the input.
 *
 * @param {Number} r The radix.
 * @param {Array} a First input number <code>a>b</code>.
 * @param {Number} ai <code>a</code> left bound.
 * @param {Number} aj <code>a</code> right bound.
 * @param {Array} b Second input number <code>b<a</code>.
 * @param {Number} bi <code>b</code> left bound.
 * @param {Number} bj <code>b</code> right bound.
 */
export default function extended_euclidean_algorithm(r, a, ai, aj, b, bi, bj) {
	const _ai = _trim_positive(a, ai, aj);
	const _bi = _trim_positive(b, bi, bj);
	const m = aj - _ai;
	const n = bj - _bi;

	if (m > n || (m === n && _cmp_n(a, _ai, aj, b, _bi) >= 0))
		return _extended_euclidean_algorithm(r, a, _ai, aj, b, _bi, bj);

	const [
		R0,
		R0i,
		T0,
		T0i,
		S0,
		S0i,
		T1,
		T1i,
		S1,
		S1i,
		steps,
	] = _extended_euclidean_algorithm(r, b, _bi, bj, a, _ai, aj);

	return [R0, R0i, S0, S0i, T0, T0i, S1, S1i, T1, T1i, steps + 1];
}

import _mul from '../../../core/arithmetic/mul/_mul.js';

import assert from 'assert';

/**
 *  Multiplies two big endian arrays and puts result in a destination array.
 *
 *  Constraints:
 *  - C is zero initialized,
 *  - |A| >= 0,
 *  - |B| >= 0,
 *  - |C| >= |A| + |B|.
 *
 * @param {Number} r base (radix)
 * @param {Array} a first operand
 * @param {Number} ai a left
 * @param {Number} aj a right
 * @param {Array} b second operand
 * @param {Number} bi b left
 * @param {Number} bj b right
 * @param {Array} c result, must be 0 initialized and be able to contain result
 * @param {Number} ci c left
 * @param {Number} cj c right
 *
 */
export default function mul(r, a, ai, aj, b, bi, bj, c, ci, cj) {
	assert(r >= 2);
	assert(ai >= 0 && aj <= a.length);
	assert(bi >= 0 && bj <= b.length);
	assert(ci >= 0 && cj <= c.length);
	assert(aj - ai >= 0);
	assert(bj - bi >= 0);
	assert(cj - ci >= aj - ai + (bj - bi));

	// TODO Truncate input if output is too small (see api/add)?

	return aj - ai >= bj - bi
		? _mul(r, a, ai, aj, b, bi, bj, c, ci, cj)
		: _mul(r, b, bi, bj, a, ai, aj, c, ci, cj);
}

import assert from 'assert';

/**
 * Subtracts 1 from a big endian array.
 *
 * Wraps on underflow. Hence, does nothing if aj <= ai.
 *
 * O(|A|) time in the worst case.
 * O(1) amortized time over any number of successive operations starting with A = O(1).
 * O(1) amortized time over O(|A|) successive operations starting with any A.
 *
 * @param {Number} r radix
 * @param {Array} a first operand
 * @param {Number} ai a left
 * @param {Number} aj a right
 */
export default function decrement(r, a, ai, aj) {
	assert(r >= 2);
	assert(ai >= 0 && aj <= a.length);

	while (--aj >= ai) {
		if (a[aj] > 0) {
			--a[aj];
			return;
		}

		a[aj] = r - 1;
	}
}

import assert from 'assert';

/**
 * Returns true if and only if input number A is 0.
 *
 * Returns true if aj <= ai.
 * O(|A|) time in the worst case.
 * O(1) time if A has no leading zero.
 *
 * @param {number[]} a first operand
 * @param {number} ai a left
 * @param {number} aj a right
 * @return {boolean} true if and only if input number is 0.
 */

export default function jz(a, ai, aj) {
	assert(ai >= 0 && aj <= a.length);

	for (; ai < aj; ++ai) if (a[ai] !== 0) return false;

	return true;
}

import convert_keep_zeros from '../../core/convert/convert_keep_zeros.js';
import trim_natural from '../../core/convert/trim_natural.js';

import assert from 'assert';

/**
 * Converts the input number A represented in base f to a number B represented
 * in base t. If A is 0 the output is B = [0], otherwise all leading zeros are
 * trimmed.
 *
 * @param {number} f the base to convert from
 * @param {number} t the base to convert to
 * @param {number[]} a the origin array
 * @param {number} ai start offset in the origin array
 * @param {number} aj end offset in the origin array
 *
 * @return {number[]} The result of the conversion.
 */
export default function convert(f, t, a, ai, aj) {
	assert(f >= 2);
	assert(t >= 2);
	assert(ai >= 0);
	assert(aj <= a.length);
	assert(aj - ai >= 0);

	const b = convert_keep_zeros(f, t, a, ai, aj);

	return trim_natural(b, 0, b.length);
}

import parse_keep_zeros from '../../core/convert/parse_keep_zeros.js';
import trim_natural from '../../core/convert/trim_natural.js';

/**
 * Converts a string representation in base f to a limb array in base t.
 *
 * @param {number} f radix of the representation
 * @param {number} t radix of the limb array
 * @param {string} string the input representation
 *
 * @return {number[]} the resulting limb array
 */
export default function parse(f, t, string) {
	const b = parse_keep_zeros(f, t, string);

	return trim_natural(b, 0, b.length);
}

import convert from './convert.js';
import _to_string from '../../core/convert/_to_string.js';

/**
 * Converts a limb array in base f to a string representation in base t.
 *
 * @param {number} f radix of the limb array
 * @param {number} t radix of the representation
 * @param {number[]} a the input limb array
 * @param {number} ai left bound of the input
 * @param {number} aj non-inclusive right bound of the input
 *
 * @return {string} the resulting representation
 */
export default function stringify(f, t, a, ai, aj) {
	if (t > 36) throw new Error('t > 36 not implemented');

	const b = convert(f, t, a, ai, aj);

	return _to_string(b);
}

import parse from './parse.js';
import stringify from './stringify.js';

/**
 * Converts a string representation in base f to a string representation in
 * base t.
 *
 * @param {number} f radix of the input
 * @param {number} t radix of the output
 * @param {string} string the input representation
 *
 * @return {string} the output representation
 */
export default function translate(f, t, string) {
	const a = parse(f, t, string);
	return stringify(t, t, a, 0, a.length);
}

import _copy from '../../array/_copy.js';

import assert from 'assert';

/**
 * Adds two big endian arrays and puts result in a destination array.
 * Wraps on overflow. |C| >= |A| >= |B|.
 *
 * @param {Number} r base (radix)
 * @param {Array} a first operand
 * @param {Number} ai a left
 * @param {Number} aj a right
 * @param {Array} b second operand
 * @param {Number} bi b left
 * @param {Number} bj b right
 * @param {Array} c result, must be 0 initialized
 * @param {Number} ci c left
 * @param {Number} cj c right
 */

export default function _add(r, a, ai, aj, b, bi, bj, c, ci, cj) {
	assert(r >= 2);
	assert(ai >= 0 && aj <= a.length);
	assert(bi >= 0 && bj <= b.length);
	assert(ci >= 0 && cj <= c.length);
	assert(cj - ci >= aj - ai);
	assert(aj - ai >= bj - bi);

	let C = 0;

	while (--bj >= bi) {
		const t = a[--aj] + b[bj] + C;
		c[--cj] = t % r;
		C = (t >= r) | 0;
	}

	if (C !== 0) {
		while (--aj >= ai && a[aj] === r - 1) c[--cj] = 0;
		if (--cj >= ci) {
			if (aj >= ai) {
				c[cj] = a[aj] + 1;
			} else c[cj] = 1;
		}
	}

	_copy(a, ai, aj, c, cj - aj + ai);
}

import assert from 'assert';

/**
 * Adds a big endian array to another.
 * Wraps on overflow. |A| >= |B|.
 *
 * @param {Number} r base (radix)
 * @param {Array} a first operand
 * @param {Number} ai a left
 * @param {Number} aj a right
 * @param {Array} b second operand
 * @param {Number} bi b left
 * @param {Number} bj b right
 */

export default function _iadd(r, a, ai, aj, b, bi, bj) {
	assert(r >= 2);
	assert(ai >= 0 && aj <= a.length);
	assert(bi >= 0 && bj <= b.length);
	assert(aj - ai >= bj - bi);

	let C = 0;

	while (--bj >= bi) {
		const T = a[--aj] + b[bj] + C;
		a[aj] = T % r;
		C = (T >= r) | 0;
	}

	if (C !== 0) {
		while (--aj >= ai && a[aj] === r - 1) a[aj] = 0;
		if (aj >= ai) ++a[aj];
	}
}

import assert from 'assert';

/**
 * Adds single limb to a big endian array.
 * Wraps on overflow.
 *
 * Input:
 *   - |A| >= 1.
 *
 * @param {Number} r base (radix)
 * @param {Number} x limb to add
 * @param {Array} a first operand
 * @param {Number} ai a left
 * @param {Number} aj a right
 */

export default function _iadd_limb(r, x, a, ai, aj) {
	assert(r >= 2);
	assert(x >= 0 && x <= r - 1);
	assert(ai >= 0 && aj <= a.length);
	assert(aj - ai >= 1);

	let T = a[--aj] + x;
	a[aj] = T % r;

	while (T >= r && --aj >= ai) {
		T = a[aj] + 1;
		a[aj] = T % r;
	}
}

import assert from 'assert';

/**
 * Divides a big endian number by a single limb number.
 * Can only work with limbs of size at most sqrt( 2^53 ).
 * Allows to prefix the dividend with an intermediate remainder.
 *
 * Does not update the remainder.
 *
 * Input
 * -----
 *  - 0 <= tmp <= d - 1
 *  - 1 <= d <= r - 1
 *  - |Q| = |D|
 *
 * @param {Number} r The radix.
 * @param {Number} tmp Intermediate remainder (MUST be <code>< d</code>).
 * @param {Number} d The divisor >= 1.
 * @param {Array} D The dividend (NOT modified).
 * @param {Number} Di Left of dividend.
 * @param {Number} Dj Right of dividend.
 * @param {Array} Q The quotient.
 * @param {Number} Qi Left of quotient.
 */
export default function _div_limb_with_prefix(r, tmp, d, D, Di, Dj, Q, Qi) {
	assert(r >= 2);
	assert(d >= 1 && d <= r - 1);
	assert(tmp >= 0 && tmp <= d - 1);
	assert(Di >= 0 && Dj <= D.length);
	assert(Qi >= 0);
	assert(Dj - Di <= Q.length - Qi);

	while (Di < Dj) {
		tmp *= r;
		tmp += D[Di];

		Q[Qi] = (tmp / d) | 0;
		tmp %= d;

		++Qi;
		++Di;
	}
}

import assert from 'assert';

import _zeros from '../../array/_zeros.js';
import _copy from '../../array/_copy.js';
import _cmp_n from '../../compare/_cmp_n.js';
import _imul_limb from '../mul/_imul_limb.js';
import _idivmod_dc_21 from './_idivmod_dc_21.js';
import _div_limb_with_prefix from './_div_limb_with_prefix.js';
import _mod_limb from './_mod_limb.js';

import jz from '../../../api/compare/jz.js';

/**
 * Input
 * -----
 *  - No leading zeros
 *  - |A| = |C|
 *  - C must be zero-initialized.
 *
 * References
 * ----------
 *   - https://gmplib.org/manual/Divide-and-Conquer-Division.html
 *
 * @param {Number} X The radix.
 * @param {Array} a Dividend / Remainder.
 * @param {Number} ai
 * @param {Number} aj
 * @param {Array} b Divisor.
 * @param {Number} bi
 * @param {Number} bj
 * @param {Array} c Quotient.
 * @param {Number} ci
 * @param {Number} cj
 */
export default function _idivmod_dc(X, a, ai, aj, b, bi, bj, c, ci, cj) {
	assert(X >= 2);
	assert(ai >= 0 && aj <= a.length);
	assert(bi >= 0 && bj <= b.length);
	assert(ci >= 0 && cj <= c.length);
	assert(aj - ai <= 0 || a[ai] !== 0);
	assert(bj - bi >= 1);
	assert(b[bi] !== 0);
	assert(cj - ci === aj - ai);

	assert(jz(c, ci, cj));

	// [BZ98] Fast Recursive Division

	const r = aj - ai;
	const s = bj - bi;

	// NB: this is the only case where c needs to be zero-initialized.
	if (r < s || (r === s && _cmp_n(a, ai, aj, b, bi) < 0)) return;

	// Shift to get n = 2^k for some k
	let _n = 1;

	while (_n < s) _n <<= 1;

	const n = _n;

	const shift = n - s;

	const x = b[bi];
	const _X = X / 2;
	const _normalize = x < _X;
	const z = Math.ceil(_X / x);

	const w = r + shift + (_normalize || a[ai] >= _X);
	const t = Math.ceil(w / n);
	const _ai = 0;
	const _aj = t * n; // + 1 if
	const _a = _zeros(_aj); // Potential normalization overflow
	const _ak = _aj - shift - r; // Or if A potentially bigger than B
	_copy(a, ai, aj, _a, _ak);

	const _bi = 0;
	const _bj = n;
	const _b = _zeros(n);
	_copy(b, bi, bj, _b, 0);

	if (_normalize) {
		_imul_limb(X, z, _a, _ai, _aj);
		_imul_limb(X, z, _b, _bi, _bj);
	}

	const _cj = _aj;
	const _c = _zeros(_cj);

	for (let i = 0; i < _aj - n; i += n) {
		_idivmod_dc_21(X, _a, i, i + (n << 1), _b, _bi, _bj, _c, i, i + (n << 1));
	}

	if (_normalize) {
		const p = _mod_limb(X, z, _a, _ai, _ak);
		_div_limb_with_prefix(X, p, z, _a, _ak, _aj - shift, a, ai, aj);
	} else {
		_copy(_a, _ak, _aj - shift, a, ai, aj);
	}

	// C is completely overwritten here
	_copy(_c, _cj - r, _cj, c, ci);
}

import assert from 'assert';

import THRESHOLD_DIV_DC from '../../thresholds/THRESHOLD_DIV_DC.js';
import _idivmod_dc_32 from './_idivmod_dc_32.js';
import _idivmod_schoolbook_large_divisor from './_idivmod_schoolbook_large_divisor.js';

import _cmp_half from '../../compare/_cmp_half.js';

/**
 * Algorithm 3.3 Divide-and-conquer division (2 by 1)
 * ==================================================
 *
 * Input
 * -----
 *  Two nonnegative integers A and B,
 *  such that A < ^n B and ^n / 2  B < ^n.
 *  n must be even if n >= THRESHOLD_DIV_DC.
 *
 *                    -----------                 -----
 *                   |  :  |  :  |               |  :  |
 *                    -----------                 -----
 *
 * Output
 * ------
 *  The quotient floor( A/B ) and the remainder A mod B.
 *
 * Complexity
 * ----------
 *  T(n) = 2T'(n/2) + K
 *
 */
export default function _idivmod_dc_21(r, a, ai, aj, b, bi, bj, c, ci, cj) {
