	assert(ai >= 0 && aj <= a.length);
	assert(bi >= 0 && bj <= b.length);
	assert(ci >= 0 && cj <= c.length);
	assert(cj - ci === aj - ai);
	assert(aj - ai === 2 * (bj - bi));
	assert(_cmp_half(r, b, bi, bj) >= 0);

	if (bj - bi < THRESHOLD_DIV_DC) {
