	// preconditions above. Hence the preconditions hold for
	// _imod_schoolbook_subroutine_do.
	if (_cmp_n(a, ai, aj - 1, b, bi) >= 0) {
		_isub(r, a, ai, aj - 1, b, bi, bj);
	}

	_imod_schoolbook_subroutine_do(r, a, ai, aj, b, bi, bj);
}

