export default function _imod_schoolbook_subroutine_do(
	r,
	a,
	ai,
	aj,
	b,
	bi,
	bj,
) {
	assert(r >= 2);
	assert(ai >= 0 && aj <= a.length);
	assert(bi >= 0 && bj <= b.length);
	assert(aj - ai === bj - bi + 1); // |a| = |b| + 1
	assert(_cmp_half(r, b, bi, bj) >= 0); // (r^n)/2 <= B < r^n
	assert(gt(b, bi, bj, a, ai, aj - 1)); // A < B * 

