export default function _copy(a, ai, aj, b, bi) {
	assert(ai >= 0 && aj <= a.length);
	assert(bi >= 0);
	assert(b.length - bi >= aj - ai);

	for (; ai < aj; ++ai, ++bi) b[bi] = a[ai];
	// While ( ai < aj ) b[++bi] = a[++ai] ;
}

