import _convert_to_larger_slow from './_convert_to_larger_slow.js';

/**
 *
 * F != t
 *
 * @param {Number} f the base to convert from
 * @param {Number} t the base to convert to
 * @param {Array} a the origin array
 * @param {Number} ai start offset in the origin array
 * @param {Number} aj end offset in the origin array
 * @param {Array} b the destination array
 * @param {Number} bi start offset in the destination array
 * @param {Number} bj end offset in the destination array
 */

export default function _convert_slow(f, t, a, ai, aj, b, bi, bj) {
	if (f > t) return _convert_to_smaller_slow(f, t, a, ai, aj, b, bi, bj);
	return _convert_to_larger_slow(f, t, a, ai, aj, b, bi, bj);
}

import _log from './_log.js';
import _convert_to_larger_fast from './_convert_to_larger_fast.js';
import _convert_to_larger_slow from './_convert_to_larger_slow.js';
import _convert_dc from './_convert_dc.js';
import THRESHOLD_CONVERT_DC from '../thresholds/THRESHOLD_CONVERT_DC.js';

import assert from 'assert';

/**
 *
 * @param {Number} f the base to convert from
 * @param {Number} t the base to convert to
 * @param {Array} a the origin array
 * @param {Number} ai start offset in the origin array
 * @param {Number} aj end offset in the origin array
 * @param {Array} b the destination array
 * @param {Number} bi start offset in the destination array
 * @param {Number} bj end offset in the destination array
 */

export default function _convert_to_larger(f, t, a, ai, aj, b, bi, bj) {
	assert(f >= 2);
	assert(t >= 2);
	assert(f < t);
	assert(ai >= 0 && aj <= a.length);
	assert(bi >= 0 && bj <= b.length);
	assert(aj - ai >= 0);
	assert(bj - bi >= 0);

	const [z, x] = _log(t, f);

	if (x === 1) return _convert_to_larger_fast(f, z, a, ai, aj, b, bi, bj);

	if (aj - ai >= THRESHOLD_CONVERT_DC) {
		// TODO use better size_small_block to avoid degenerated small blocks
		// that slow down the execution
		return _convert_dc(THRESHOLD_CONVERT_DC >> 1, f, t, a, ai, aj, b, bi, bj);
	}

	return _convert_to_larger_slow(f, t, a, ai, aj, b, bi, bj);
}

import assert from 'assert';

/**
 *
 * @param {Number} ar the base to convert from
 * @param {Number} z if br is the base to convert to then log(br) = z log(ar)
 * @param {Array} a the origin array
 * @param {Number} ai start offset in the origin array
 * @param {Number} aj end offset in the origin array
 * @param {Array} b the destination array
 * @param {Number} bi start offset in the destination array
 * @param {Number} bj end offset in the destination array
 */

export default function _convert_to_larger_fast(ar, z, a, ai, aj, b, bi, bj) {
	assert(ar >= 2);
	assert(ai >= 0 && aj <= a.length);
	assert(bi >= 0 && bj <= b.length);
	assert(aj - ai >= 0);
	assert(bj - bi >= 0);

	let i;
	let t;

	const m = aj - ai;
	const n = bj - bi;

	// Number of parts of first
	// destination block if incomplete
	const r = m % z;

	// Number of complete blocks in destination
	const q = (m / z) | 0;

	// Total number of blocks in destination
	// (complete ones + first if incomplete)
	const w = q + !!r;

	if (n >= w) {
		// If destination can contain more than
		// what is available in source then
		// compute the effective write start
		// in destination and set i to the correct
		// offset according to the size
		// (in source blocks) of the
		// first destination block if incomplete
		bi = bj - w;
		i = (z - r) % z;
	} else {
		// If source contains more than what
		// destination can handle set the effective
		// read start in source and set i to 0 because
		// all blocks will be complete
		ai = aj - n * z;
		i = 0;
	}

	for (; ai < aj && bi < bj; ++bi) {
		t = 0;
		for (; i < z; ++i) {
			t *= ar; // Aggregate source blocks
			t += a[ai]; // Using simple
			++ai; // Multiply + add
		}

		b[bi] = t; // Set block in destination
		i = 0;
	}
}

import _iadd_limb from '../arithmetic/add/_iadd_limb.js';
import _imul_limb from '../arithmetic/mul/_imul_limb.js';

import assert from 'assert';

/**
 *
 * O(N^2). f < t.
 *
 * @param {Number} f the base to convert from
 * @param {Number} t the base to convert to
 * @param {Array} a the origin array
 * @param {Number} ai start offset in the origin array
 * @param {Number} aj end offset in the origin array
 * @param {Array} b the destination array
 * @param {Number} bi start offset in the destination array
 * @param {Number} bj end offset in the destination array
 */

export default function _convert_to_larger_slow(f, t, a, ai, aj, b, bi, bj) {
	assert(f >= 2);
	assert(f <= t);
	assert(ai >= 0 && aj <= a.length);
	assert(bi >= 0 && bj <= b.length);
	assert(aj - ai >= 0);
	assert(bj - bi >= 0);

	let batch = 1;
	let shift = f;
	for (; shift * f <= t; shift *= f, ++batch);

	const rounds = ((aj - ai) / batch) | 0;
	const first = (aj - ai) % batch;

	if (first > 0) {
		let w1 = a[ai];
		for (let j = 1; j < first; ++j) {
			w1 *= f;
			w1 += a[ai + j];
		}

		b[bj - 1] = w1;
	}

	const _ai = ai + first;
	let _bi = bj - 1;

	for (let i = 0; i < rounds; ++i) {
		if (b[_bi] !== 0 && _bi > bi) --_bi;
		_imul_limb(t, shift, b, _bi, bj);
		if (b[_bi] !== 0 && _bi > bi) --_bi;
		let w = 0;
		let j = _ai + i * batch;
		const _end = j + batch;
		do {
			w *= f;
			w += a[j];
		} while (++j < _end);

		_iadd_limb(t, w, b, _bi, bj);
	}
}

