	assert(bi >= 0 && bj <= b.length);
	assert(aj - ai >= 0);
	assert(bj - bi >= 0);

	const [z, x] = _log(f, t);

	if (x === 1) return _convert_to_smaller_fast(t, z, a, ai, aj, b, bi, bj);

	if (aj - ai >= THRESHOLD_CONVERT_DC) {
