 * @param {Number} t the base to convert to
 * @param {Array} a the origin array
 * @param {Number} ai start offset in the origin array
 * @param {Number} aj end offset in the origin array
 * @param {Array} b the destination array
 * @param {Number} bi start offset in the destination array
 * @param {Number} bj end offset in the destination array
 */

export default function _convert_to_smaller_slow(f, t, a, ai, aj, b, bi, bj) {
	assert(f >= t);
	assert(ai >= 0 && aj <= a.length);
	assert(bi >= 0 && bj <= b.length);
	assert(aj - ai >= 1);
	assert(bj - bi >= aj - ai);

	let batch = 1;
	let shift = t;
	for (; shift * t <= f; shift *= t, ++batch);

	const m = aj - ai;
	let q = _alloc(m);
	let r = _alloc(m); // NOTE that this copy is unnecessary when
	_copy(a, ai, aj, r, 0); // Called from parse since we can discard it.

	let i = 0;

	while (true) {
		_idivmod_limb(f, shift, r, i, m, q, i);

		const end = Math.max(bi, bj - batch);
		let block = r[m - 1];

		do {
			b[--bj] = block % t;
			block = (block / t) | 0;
		} while (bj > end);

		i = _trim_positive(q, i, m);
		if (i === m) return;

		// _copy( q , i , m , r , i ) ;
		const tmp = q;
		q = r;
		r = tmp;
	}
}

import _int from './_int.js';

/**
 * Converts a string representation to a limb array representation without
 * radix conversion.
 *
 * @param {string} string input string
 *
 * @return {number[]} output limb array
 */
export default function _from_string(string) {
	const n = string.length;

	const a = [];

	for (let k = 0; k < n; ++k) a.push(_int(string[k]));

	return a;
}

/**
 * Converts an input character representation to a limb.
 *
 * @param {string} x input character
 *
 * @return {number} output limb
 */
export default function _int(x) {
	if (x >= '0' && x <= '9') return x.charCodeAt(0) - 48;
	if (x >= 'A' && x <= 'Z') return x.charCodeAt(0) - 55;
	if (x >= 'a' && x <= 'z') return x.charCodeAt(0) - 87;

	throw new Error('invalid literal for _int: ' + x);
}

import assert from 'assert';

export default function _log(x, y) {
	assert(y > 1);

	let z = 0;

	while (x >= y) {
		if (x % y) break;
		x /= y;
		++z;
	}

	return [z, x];
}

import _chr from './_chr.js';

/**
 * Convert an entire limb array to a string representation (without changing
 * the radix).
 *
 * @param {number[]} b The inpurt limb array.
 *
 * @return {string} The string representation.
 */
export default function _to_string(b) {
	const n = b.length;

	const data = [];

	for (let k = 0; k < n; ++k) data.push(_chr(b[k]));

	return data.join('');
}

import assert from 'assert';

/**
 * Compute the new inclusive left bound of a limb array by skipping all
 * leading zeros.
 *
 * @param {number[]} a The input limb array.
 * @param {number} ai
 * @param {number} aj
 *
 * @return {number} The new inclusive left bound of the input.
 */
export default function _trim_positive(a, ai, aj) {
	assert(ai >= 0 && aj <= a.length);

	while (ai < aj && a[ai] === 0) ++ai;

	return ai;
}

import _zeros from '../array/_zeros.js';
import _convert from './_convert.js';

/**
 * Converts the input number A represented in base f to a number B represented
 * in base t. All leading zeros resulting from the conversion are kept.
 *
 * @param {number} f the base to convert from
 * @param {number} t the base to convert to
 * @param {number[]} a the origin array
 * @param {number} ai start offset in the origin array
 * @param {number} aj end offset in the origin array
 *
 * @return {number[]} The resulting limb array.
 */
export default function convert_keep_zeros(f, t, a, ai, aj) {
	const bi = 0;
	const bj = Math.ceil((Math.log(f) / Math.log(t)) * (aj - ai));
	const b = _zeros(bj - bi);

	_convert(f, t, a, ai, aj, b, bi, bj);

	return b;
}

import _from_string from './_from_string.js';
import convert_keep_zeros from './convert_keep_zeros.js';

/**
 * Converts a string representation in base f to a limb array in base t keeping
 * all leading zeros resulting from the conversion process.
 *
 * @param {number} f radix of the representation
 * @param {number} t radix of the limb array
 * @param {string} string the input representation
 *
 * @return {number[]} the resulting limb array
 */
export default function parse_keep_zeros(f, t, string) {
	if (f > 36) throw new Error('f > 36 not implemented');

	const a = _from_string(string);

	return convert_keep_zeros(f, t, a, 0, a.length);
}

import _trim_positive from './_trim_positive.js';
import _alloc from '../array/_alloc.js';
import _copy from '../array/_copy.js';

/**
 * Trim a limb array so that it is either [0] or does not start with any
 * leading zeros. Return a newly allocated array and does not modify the input.
 *
 * @param {number[]} a The input limb array.
 * @param {number} ai
 * @param {number} aj
 *
 * @return {number[]} The input but trimmed.
 */
export default function trim_natural(a, ai, aj) {
	const x = _trim_positive(a, ai, aj);

	if (x >= aj) return [0];

	const b = _alloc(aj - x);

	_copy(a, x, aj, b, 0);

	return b;
}

